import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [stats, setStats] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const baseId = 1; // can be dynamic later

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/assets/dashboard?base_id=${baseId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setStats(response.data);
    } catch (err) {
      console.error('Failed to fetch dashboard stats:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome to Military Asset Management Dashboard</h1>

      {stats ? (
        <div style={{ marginTop: '20px' }}>
          <h2>Base {baseId} Summary</h2>
          <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Opening Balance</th>
                <th>Transfers In</th>
                <th>Transfers Out</th>
                <th>Purchases</th>
                <th>Net Movement</th>
                <th>Closing Balance</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{stats.opening_balance}</td>
                <td>{stats.transfer_in}</td>
                <td>{stats.transfer_out}</td>
                <td>{stats.purchases}</td>
                <td>{stats.net_movement}</td>
                <td>{stats.closing_balance}</td>
              </tr>
            </tbody>
          </table>

          {/* Net Movement Popup Button */}
          <button
            style={{ marginTop: '20px' }}
            onClick={() => setShowPopup(true)}
          >
            View Net Movement Details
          </button>

          {/* Popup */}
          {showPopup && (
            <div style={{
              position: 'fixed',
              top: '20%',
              left: '30%',
              background: '#fff',
              padding: '20px',
              border: '2px solid #333',
              zIndex: 999
            }}>
              <h3>Net Movement Details</h3>
              <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse' }}>
                <tbody>
                  <tr><td>Opening Balance</td><td>{stats.opening_balance}</td></tr>
                  <tr><td>Purchases</td><td>{stats.purchases}</td></tr>
                  <tr><td>Transfers In</td><td>{stats.transfer_in}</td></tr>
                  <tr><td>Transfers Out</td><td>{stats.transfer_out}</td></tr>
                  <tr><td>Net Movement</td><td>{stats.net_movement}</td></tr>
                  <tr><td>Closing Balance</td><td>{stats.closing_balance}</td></tr>
                </tbody>
              </table>
              <button onClick={() => setShowPopup(false)} style={{ marginTop: '10px' }}>Close</button>
            </div>
          )}

        </div>
      ) : (
        <p>Loading dashboard stats...</p>
      )}
    </div>
  );
}

export default Dashboard;
