import React, { useState } from 'react';
import axios from 'axios';

function AssetExpenditure() {
  const [formData, setFormData] = useState({
    asset_id: '',
    quantity: '',
    expended_date: ''
  });

  const handleChange = (e) => {
    setFormData({ 
      ...formData, 
      [e.target.name]: e.target.value 
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/assets/expend', formData);
      alert('Asset Expenditure recorded successfully!');
      setFormData({ asset_id: '', quantity: '', expended_date: '' });
    } catch (error) {
      console.error('Expenditure Error:', error);
      alert('Failed to record expenditure');
    }
  };

  return (
    <div>
      <h2>Asset Expenditure</h2>
      <form onSubmit={handleSubmit}>
        <label>Asset ID:</label>
        <input type="number" name="asset_id" value={formData.asset_id} onChange={handleChange} required />

        <label>Quantity:</label>
        <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} required />

        <label>Expended Date:</label>
        <input type="date" name="expended_date" value={formData.expended_date} onChange={handleChange} required />

        <button type="submit">Submit Expenditure</button>
      </form>
    </div>
  );
}

export default AssetExpenditure;
