import React, { useState } from 'react';
import axios from 'axios';

function AssetExpenditure() {
  const [assetId, setAssetId] = useState('');
  const [quantity, setQuantity] = useState('');
  const [expendedDate, setExpendedDate] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');

      const data = {
        asset_id: parseInt(assetId),
        quantity: parseInt(quantity),
        expended_date: expendedDate,
      };

      await axios.post('http://localhost:5000/api/assets/expend', data, {
    headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
});


      alert('Asset expenditure recorded successfully!');
      setAssetId(''); setQuantity(''); setExpendedDate('');
    } catch (error) {
      console.error('Error recording expenditure:', error);
      alert('Error recording expenditure');
    }
  };

  return (
    <div>
      <h2>Expend Asset</h2>
      <form onSubmit={handleSubmit}>
        <input type="number" value={assetId} onChange={e => setAssetId(e.target.value)} placeholder="Asset ID" required />
        <input type="number" value={quantity} onChange={e => setQuantity(e.target.value)} placeholder="Quantity Expended" required />
        <input type="date" value={expendedDate} onChange={e => setExpendedDate(e.target.value)} required />
        <button type="submit">Submit Expenditure</button>
      </form>
    </div>
  );
}

export default AssetExpenditure;
