import React, { useEffect, useState } from 'react';
import axios from 'axios';

function TransactionHistory() {
  const [purchases, setPurchases] = useState([]);
  const [type, setType] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const [transfers, setTransfers] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [expenditures, setExpenditures] = useState([]);

  const [purchasesPage, setPurchasesPage] = useState(1);
  const [transfersPage, setTransfersPage] = useState(1);
  const [assignmentsPage, setAssignmentsPage] = useState(1);
  const [expendituresPage, setExpendituresPage] = useState(1);

  const limit = 5;
  const token = localStorage.getItem('token');

  const fetchTransactions = async () => {
    try {
      const purchaseRes = await axios.get(`http://localhost:5000/api/assets/purchases`, {
        headers: { Authorization: `Bearer ${token}` },
        params: {
          type,
          startDate,
          endDate
        }
      });
      setPurchases(purchaseRes.data);

      const transferRes = await axios.get(`http://localhost:5000/api/assets/transfers`, {
        headers: { Authorization: `Bearer ${token}` },
        params: {
          page: transfersPage,
          limit
        }
      });
      setTransfers(transferRes.data);

      const assignmentRes = await axios.get(`http://localhost:5000/api/assets/assignments`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAssignments(assignmentRes.data);

      const expenditureRes = await axios.get(`http://localhost:5000/api/assets/expenditures`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setExpenditures(expenditureRes.data);

    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, [purchasesPage, transfersPage, assignmentsPage, expendituresPage]);

  const handleApplyFilters = () => {
    fetchTransactions();
  };

  // Pagination handlers
  const handlePrev = (setter, page) => {
    if (page > 1) setter(page - 1);
  };

  const handleNext = (setter, page) => {
    setter(page + 1);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Transaction History</h2>

      {/* Purchases Filters */}
      <div style={{ marginBottom: '10px' }}>
        <label>Type: </label>
        <select value={type} onChange={e => setType(e.target.value)}>
          <option value="">All</option>
          <option value="Weapon">Weapon</option>
          <option value="Vehicle">Vehicle</option>
        </select>

        <label style={{ marginLeft: '10px' }}>From: </label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />

        <label style={{ marginLeft: '10px' }}>To: </label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />

        <button onClick={handleApplyFilters} style={{ marginLeft: '10px' }}>Apply Filters</button>
      </div>

      {/* Purchases Table */}
      <h3>Purchases</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Purchase ID</th>
            <th>Asset Name</th>
            <th>Quantity</th>
            <th>Purchased On</th>
          </tr>
        </thead>
        <tbody>
          {purchases.map((p, index) => (
            <tr key={index}>
              <td>{p.asset_id}</td>
              <td>{p.asset_name}</td>
              <td>{p.quantity}</td>
              <td>{p.purchased_on}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Transfers Table */}
      <h3>Transfers</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Transfer ID</th>
            <th>Asset ID</th>
            <th>From Base</th>
            <th>To Base</th>
            <th>Quantity</th>
            <th>Transfer Date</th>
          </tr>
        </thead>
        <tbody>
          {transfers.map((t, index) => (
            <tr key={index}>
              <td>{t.transfer_id}</td>
              <td>{t.asset_id}</td>
              <td>{t.from_base_id}</td>
              <td>{t.to_base_id}</td>
              <td>{t.quantity}</td>
              <td>{t.transfer_date}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={() => handlePrev(setTransfersPage, transfersPage)}>Previous</button>
      <button onClick={() => handleNext(setTransfersPage, transfersPage)}>Next</button>

      {/* Assignments Table */}
      <h3>Assignments</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Assignment ID</th>
            <th>Asset ID</th>
            <th>Personnel Name</th>
            <th>Quantity</th>
            <th>Assigned On</th>
          </tr>
        </thead>
        <tbody>
          {assignments.map((a, index) => (
            <tr key={index}>
              <td>{a.assignment_id}</td>
              <td>{a.asset_id}</td>
              <td>{a.personnel_name}</td>
              <td>{a.quantity}</td>
              <td>{a.assigned_date}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Expenditures Table */}
      <h3>Expenditures</h3>
      <table border="1">
        <thead>
          <tr>
            <th>Expenditure ID</th>
            <th>Asset ID</th>
            <th>Quantity</th>
            <th>Expended On</th>
          </tr>
        </thead>
        <tbody>
          {expenditures.map((e, index) => (
            <tr key={index}>
              <td>{e.expenditure_id}</td>
              <td>{e.asset_id}</td>
              <td>{e.quantity}</td>
              <td>{e.expended_date}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionHistory;
