import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [stats, setStats] = useState(null);
  const baseId = 1; // can be dynamic later

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`http://localhost:5000/api/assets/dashboard?base_id=${baseId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setStats(response.data);
    } catch (err) {
      console.error('Failed to fetch dashboard stats:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome to Military Asset Management Dashboard</h1>

      {stats ? (
        <div style={{ marginTop: '20px' }}>
          <h2>Base {baseId} Summary</h2>
          <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Opening Balance</th>
                <th>Transfers In</th>
                <th>Transfers Out</th>
                <th>Purchases</th>
                <th>Net Movement</th>
                <th>Closing Balance</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{stats.opening_balance}</td>
                <td>{stats.transfer_in}</td>
                <td>{stats.transfer_out}</td>
                <td>{stats.purchases}</td>
                <td>{stats.net_movement}</td>
                <td>{stats.closing_balance}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading dashboard stats...</p>
      )}
    </div>
  );
}

export default Dashboard;
