import React, { useState } from 'react';
import axios from 'axios';

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    role: 'admin',      // default role, or use a select input
    base_id: 1          // default base_id, or select input
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/signup', formData);
      alert('Signup successful! Please login.');
      window.location.href = '/login';
    } catch (error) {
      console.error(error);
      alert('Signup failed. ' + (error.response?.data?.message || 'Server error'));
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Signup</h2>
      <form onSubmit={handleSubmit}>
        <input name="username" type="text" placeholder="Username" onChange={handleChange} required /><br />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} required /><br />
        <input name="role" type="text" placeholder="Role (admin/logistics/commander)" onChange={handleChange} required /><br />
        <input name="base_id" type="number" placeholder="Base ID" onChange={handleChange} required /><br />
        <button type="submit">Signup</button>
      </form>
    </div>
  );
}

export default Signup;
