import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [stats, setStats] = useState(null);
  const [baseId] = useState(1); // static now
  const [fromDate, setFromDate] = useState('');
  const [toDate, setToDate] = useState('');
  const [equipmentType, setEquipmentType] = useState('');

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const params = {
        base_id: baseId,
        from_date: fromDate,
        to_date: toDate,
        type: equipmentType
      };

      const response = await axios.get(`http://localhost:5000/api/assets/dashboard`, {
        headers: { Authorization: `Bearer ${token}` },
        params
      });
      setStats(response.data);
    } catch (err) {
      console.error('Failed to fetch dashboard stats:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome to Military Asset Management Dashboard</h1>

      <div style={{ margin: '20px 0' }}>
        <label>From: </label>
        <input type="date" value={fromDate} onChange={(e) => setFromDate(e.target.value)} />

        <label style={{ marginLeft: '10px' }}>To: </label>
        <input type="date" value={toDate} onChange={(e) => setToDate(e.target.value)} />

        <label style={{ marginLeft: '10px' }}>Equipment Type: </label>
        <input type="text" placeholder="Weapon / Vehicle" value={equipmentType} onChange={(e) => setEquipmentType(e.target.value)} />

        <button style={{ marginLeft: '10px' }} onClick={fetchDashboardStats}>Apply Filters</button>
      </div>

      {stats ? (
        <div>
          <h2>Base {baseId} Summary</h2>
          <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th>Opening Balance</th>
                <th>Transfers In</th>
                <th>Transfers Out</th>
                <th>Purchases</th>
                <th>Net Movement</th>
                <th>Closing Balance</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{stats.opening_balance}</td>
                <td>{stats.transfer_in}</td>
                <td>{stats.transfer_out}</td>
                <td>{stats.purchases}</td>
                <td>{stats.net_movement}</td>
                <td>{stats.closing_balance}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading dashboard stats...</p>
      )}
    </div>
  );
}

export default Dashboard;
